DROP TABLE IF EXISTS `recipes`;
CREATE TABLE `recipes` (
  `id` int(8) NOT NULL DEFAULT '0',
  `name` varchar(40) DEFAULT NULL,
  `item` int(8) NOT NULL DEFAULT '0',
  `foundation` int(11) NOT NULL DEFAULT '0',
  `q` int(8) NOT NULL DEFAULT '0',
  `lvl` int(8) NOT NULL DEFAULT '0',
  `success` int(8) NOT NULL DEFAULT '0',
  `recid` int(8) NOT NULL DEFAULT '0',
  `mp` int(8) NOT NULL DEFAULT '0',
  `exp` int(10) NOT NULL DEFAULT '0',
  `sp` int(10) NOT NULL DEFAULT '0',
  `dwarven` int(10) NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`),
  KEY `item` (`item`),
  KEY `success` (`success`),
  KEY `exp` (`exp`),
  KEY `sp` (`sp`),
  KEY `lvl` (`lvl`),
  KEY `recid` (`recid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

